
import base64


def save_base64_img(base64_str, save_path):
    base64_str = base64_str[22:]
    img_data = base64.b64decode(base64_str)
    with open(save_path,'wb') as f:
        f.write(img_data)



if __name__ == "__main__":
    base64_str = "data:image/png;base64,"
    save_path = r"C:\Users\Administrator\Desktop\s.png"
    save_base64_img(base64_str, save_path)